-- Create the main table (must be created first because foreign keys)
CREATE TABLE users (
    username VARCHAR(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
    password VARCHAR(100) NOT NULL,
    last_login BIGINT DEFAULT 0,
    last_claim BIGINT DEFAULT 0,
    tokens INT DEFAULT 0,
    token_doubler INT DEFAULT 0,
    coins INT DEFAULT 0,
    trade_credits INT DEFAULT 0,
    level INT DEFAULT 1,
    points BIGINT DEFAULT 0,
    active_avatar VARCHAR(200) NOT NULL,
    brawlers TEXT NOT NULL,
    avatars TEXT NOT NULL,
    themes TEXT NOT NULL,
    scenes TEXT NOT NULL,
    accessories TEXT NOT NULL,
    wild_card_pins TEXT NOT NULL,
    featured_item VARCHAR(255) NOT NULL,
    PRIMARY KEY (username))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4 COLLATE utf8mb4_bin;

CREATE INDEX frank ON users (tokens, token_doubler, coins, trade_credits);
CREATE INDEX frnk ON users (password);
CREATE INDEX primo ON users (last_login);
CREATE INDEX darryl ON users (last_claim);
CREATE INDEX daryl ON users (tokens);
CREATE INDEX dryl ON users (token_doubler);
create INDEX ash on users (featured_item);
