-- Create the trades table
CREATE TABLE trades (
    tradeid BIGINT NOT NULL AUTO_INCREMENT,
    creator VARCHAR(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
    creator_avatar VARCHAR(200) NOT NULL,
    creator_color VARCHAR(30) NOT NULL,
    offer TEXT NOT NULL,
    request TEXT NOT NULL,
    trade_credits INT DEFAULT 1,
    trade_credits_time INT DEFAULT 0,
    expiration BIGINT NOT NULL,
    accepted TINYINT DEFAULT 0,
    accepted_by VARCHAR(30) DEFAULT '',
    PRIMARY KEY (tradeid))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4 COLLATE utf8mb4_bin;
